operations.body:
query AllEvents {
  allEvents {
    id
    title
    date
    __typename
  }
}

query AllHotels {
  allHotels {
    id
    name
    avgCost
    __typename
  }
}

query AllLifts {
  allLifts {
    id
    name
    status
    capacity
    __typename
  }
}

query LiftOfTheWeek($id: ID!) {
  findLiftById(id: $id) {
    id
    name
    ...LiftDetails
    __typename
  }
}

fragment LiftDetails on Lift {
  status
  night
  capacity
  __typename
}

subscription ListenForStatus {
  liftStatusChange {
    name
    id
    status
    __typename
  }
}

mutation SetLiftStatus($id: ID!, $status: LiftStatus!) {
  setLiftStatus(id: $id, status: $status) {
    id
    name
    status
    __typename
  }
}
